namespace.insert:
insert into department
(AggregateId,
Name,
Descn,
CreateDate,
Creator,
EditDate,
Editor,
Version)
values
(@AggregateId,
@Name,
@Descn,
@CreateDate,
@Creator,
@EditDate,
@Editor,
@Version);

  select last_insert_rowid() as Id;

namespace.delete:
delete from department
where AggregateId = @AggregateId
and Version = @Version;
